import React, { useState } from 'react'
import { useHistory } from 'react-router-dom'
import { Formik, Field } from 'formik'
import DatePicker from 'react-datepicker'
import Server from '../../utils/server'
import { Modal, ModalHeader, ModalBody, ModalFooter, Button, Input, Label, Select, Textarea } from '@windmill/react-ui'

import "react-datepicker/dist/react-datepicker.css";

function AddTransaction(props) {
  const [isModalOpen, setIsModalOpen] = useState(false)

  let history = useHistory()

  function openModal() {
    setIsModalOpen(true)
  }

  function closeModal() {
    setIsModalOpen(false)
  }

  return (
      <div className="px-6 my-6">
        <Button onClick={openModal}>
          Add Transaction
          <span className="ml-2" aria-hidden="true">
            +
          </span>
        </Button>
        <Modal isOpen={isModalOpen} onClose={closeModal}>
            <Formik
                initialValues={
                    { 
                        blockId: '',
                        txid: '', 
                        balance_change: '',
                        address: '',
                        network_fee: '',
                        size: '',
                        description: '',
                        account: '',
                        categoryid: '',
                        transactiontypeId: '',
                        payee: ''
                    }
                }
                validate={values => {
                    const errors = {};
                    if (!values.xpub) {
                        errors.xpub = 'Required';
                    } 
                    if (!values.name) {
                        errors.name = 'Required';
                    }
                    if (!values.birthday) {
                        errors.birthday = 'Required';
                    }
                    if (!values.accountType) {
                        errors.accountType = 'Required';
                    }
                    return errors;
                }}
                onSubmit={(values, { setSubmitting }) => {
                    setTimeout(() => {
                        Server.newAccount(JSON.stringify(values, null, 2));
                        setSubmitting(false);
                        closeModal();
                        history.push('/app/accounts');
                        // TODO: make sure the accounts data pulls fresh from DB (actually, res should send new object back)
                    }, 400)
                }}

            >

                {({
                    values,
                    errors,
                    touched,
                    handleChange,
                    handleBlur,
                    handleSubmit,
                    isSubmitting,
                    /* and other goodies */
                }) => (
                    <form onSubmit={handleSubmit}>
                        <ModalHeader>Add transaction</ModalHeader>
                        <ModalBody>
                            <div className="grid gap-6 mb-8 md:grid-cols-2 bg-white rounded-lg shadow-md dark:bg-gray-800">

                                <Label>
                                    <span>Sender</span>
                                    <Input 
                                        className="mt-1" 
                                        placeholder="Wasabi Wallet" 
                                        name="name" 
                                        id="name" 
                                        onChange={handleChange}
                                        onBlur={handleBlur}
                                        value={values.name} 
                                    />
                                    {errors.name && touched.name && errors.name}
                                </Label>

                                <Label>
                                    <span>Recipient</span>
                                    <Input 
                                        className="mt-1" 
                                        placeholder="Citadel21" 
                                        name="xpub" 
                                        id="xpub" 
                                        onChange={handleChange}
                                        onBlur={handleBlur}
                                        value={values.xpub} 
                                    />

                                    {errors.xpub && touched.xpub && errors.xpub}
                                </Label>

                                {/* TODO: DATEPICKER */}
                                <Label className="mt-4">
                                    <span>Date</span>
                                    {/*<DatePicker
                                        className="mt-1" 
                                        name="birthday" 
                                        id="birthday"
                                        onChange={handleChange}
                                        onBlur={handleBlur}
                                        value={values.birthday} 
                                    />*/}
                                    <Input 
                                        className="mt-1" 
                                        placeholder="TODO: Datepicker. Format: Date e.g 12/12/2012" 
                                        name="birthday" 
                                        id="birthday" 
                                        onChange={handleChange}
                                        onBlur={handleBlur}
                                        value={values.birthday} 
                                    />
                                    {errors.birthday && touched.birthday && errors.birthday}
                                </Label>

                                <Label className="mt-4">
                                    <span>Category</span>
                                    <Input 
                                        className="mt-1" 
                                        rows="3" 
                                        placeholder="123,098" 
                                        name="notes" 
                                        id="notes" 
                                        onChange={handleChange}
                                        onBlur={handleBlur}
                                        value={values.notes} 
                                    />
                                    {errors.notes && touched.notes && errors.notes}
                                </Label>

                                <Label className="mt-4">
                                    <span>Amount</span>
                                    <Input 
                                        className="mt-1" 
                                        rows="3" 
                                        placeholder="123,098" 
                                        name="notes" 
                                        id="notes" 
                                        onChange={handleChange}
                                        onBlur={handleBlur}
                                        value={values.notes} 
                                    />
                                    {errors.notes && touched.notes && errors.notes}
                                </Label>

                                <Label className="mt-4">
                                    <span>Fee</span>
                                    <Input 
                                        className="mt-1" 
                                        rows="3" 
                                        placeholder="172" 
                                        name="notes" 
                                        id="notes" 
                                        onChange={handleChange}
                                        onBlur={handleBlur}
                                        value={values.notes} 
                                    />
                                    {errors.notes && touched.notes && errors.notes}
                                </Label>

                                <Label className="mt-4">
                                    <span>Memo</span>
                                    <Input 
                                        className="mt-1" 
                                        rows="3" 
                                        placeholder="Write any useful information about your account here." 
                                        name="notes" 
                                        id="notes" 
                                        onChange={handleChange}
                                        onBlur={handleBlur}
                                        value={values.notes} 
                                    />
                                    {errors.notes && touched.notes && errors.notes}
                                </Label>
                                <br />
                            </div>
                        </ModalBody>
                        <ModalFooter>
                            {/* I don't like this approach. Consider passing a prop to ModalFooter
                            * that if present, would duplicate the buttons in a way similar to this.
                            * Or, maybe find some way to pass something like size="large md:regular"
                            * to Button
                            */}
                            <div className="hidden sm:block">
                                <Button layout="outline" onClick={closeModal}>
                                    Cancel
                                </Button>
                            </div>
                            <div className="hidden sm:block">
                                <Button type="submit" disabled={isSubmitting}>Create Transaction</Button>
                            </div>
                            <div className="block w-full sm:hidden">
                                <Button block size="large" layout="outline" onClick={closeModal}>
                                    Cancel
                                </Button>
                            </div>
                            <div className="block w-full sm:hidden">
                                <Button block size="large" type="submit" disabled={isSubmitting}>
                                    Create Transaction
                                </Button>
                            </div>
                        </ModalFooter>
                    </form>
                )}
            </Formik>
        </Modal>
      </div>
  )
}

export default AddTransaction
