import React, { useState } from 'react'
import { Formik } from 'formik'
import { Modal, ModalHeader, ModalBody, ModalFooter, Button, Input, Label, Select, Textarea } from '@windmill/react-ui'

function FormikTest() {
  const [isModalOpen, setIsModalOpen] = useState(false)

  function openModal() {
    setIsModalOpen(true)
  }

  function closeModal() {
    setIsModalOpen(false)
  }

  function onClick() {

  }

  return (
      <div className="px-6 my-6">
        <Button onClick={openModal}>
          Formik Test
          <span className="ml-2" aria-hidden="true">
            +
          </span>
        </Button>
        <Modal isOpen={isModalOpen} onClose={closeModal}>
        <Formik
            initialValues={{ email: '', password: '' }}
            validate={values => {
                const errors = {};
                if (!values.email) {
                    errors.email = 'Required';
                } else if (
                    !/^[A-Z0-9._%+-]+@[A-Z0-9.-]+\.[A-Z]{2,}$/i.test(values.email)
                ) {
                    errors.email = 'Invalid email address';
                }
                return errors;
            }}

            onSubmit={(values, { setSubmitting }) => {

            setTimeout(() => {

                alert(JSON.stringify(values, null, 2));

                setSubmitting(false);

            }, 400);

            }}

            >

            {({

            values,

            errors,

            touched,

            handleChange,

            handleBlur,

            handleSubmit,

            isSubmitting,

            /* and other goodies */

            }) => (

            <form onSubmit={handleSubmit}>
                <ModalHeader>Add account</ModalHeader>
                <ModalBody>

                <input

                type="email"

                name="email"

                onChange={handleChange}

                onBlur={handleBlur}

                value={values.email}

                />

                {errors.email && touched.email && errors.email}

                <input

                type="password"

                name="password"

                onChange={handleChange}

                onBlur={handleBlur}

                value={values.password}

                />

                {errors.password && touched.password && errors.password}
                </ModalBody>

                <button type="submit" disabled={isSubmitting}>

                Submit

                </button>

            </form>

            )}

            </Formik>
        <form action="/accounts/add" method="POST">
          <ModalHeader>Add account</ModalHeader>
          <ModalBody>
            <div className="px-4 py-3 mb-8 bg-white rounded-lg shadow-md dark:bg-gray-800">

              <Label>
                <span>Account name</span>
                <Input className="mt-1" placeholder="Wasabi Wallet" name="name" id="name" /*value={this.state.name}*/ />
              </Label>

              <Label>
                <span>Public Key</span>
                <Input className="mt-1" placeholder="xpub5hjgms...98fgdbjnfg" />
              </Label>

              <div className="mt-4">
                {/* TODO: Check if this label is accessible, or fallback */}
                {/* <span className="text-sm text-gray-700 dark:text-gray-400">Account Type</span> */}
                <Label>Account Type</Label>
                <div className="mt-2">
                  <Label radio>
                    <Input type="radio" value="wallet" name="accountType" />
                    <span className="ml-2">Hot wallet</span>
                  </Label>
                  <Label className="ml-6" radio>
                    <Input type="radio" value="vault" name="accountType" />
                    <span className="ml-2">Vault</span>
                  </Label>
                  <Label className="ml-6" radio>
                    <Input disabled type="radio" value="lightning" name="accountType" />
                    <span className="ml-2">Lightning (soon™)</span>
                  </Label>
                </div>
              </div>

              <Label className="mt-4">
                <span>Setup</span>
                <Select className="mt-1">
                  <option>Multisig</option>
                  <option>P2PKH</option>
                  <option>P2SH</option>
                  <option>Single Address</option>
                </Select>
              </Label>

              <Label className="mt-4">
                <span>Notes</span>
                <Textarea className="mt-1" rows="3" placeholder="Write any useful information about your account here." />
              </Label>

            </div>
          </ModalBody>
          <ModalFooter>
            {/* I don't like this approach. Consider passing a prop to ModalFooter
            * that if present, would duplicate the buttons in a way similar to this.
            * Or, maybe find some way to pass something like size="large md:regular"
            * to Button
            */}
            <div className="hidden sm:block">
              <Button layout="outline" onClick={closeModal}>
                Cancel
              </Button>
            </div>
            <div className="hidden sm:block">
              <Button type="submit" onClick={onClick}>Create Account</Button>
            </div>
            <div className="block w-full sm:hidden">
              <Button block size="large" layout="outline" onClick={closeModal}>
                Cancel
              </Button>
            </div>
            <div className="block w-full sm:hidden">
              <Button block size="large" type="submit" onClick={onClick}>
                Create Account
              </Button>
            </div>
          </ModalFooter>
          </form>
        </Modal>
      </div>
  )
}

export default FormikTest
