import React, { useState, useEffect } from 'react'
import { EditIcon, MenuIcon } from '../icons'

import {
    TableBody,
    TableContainer,
    Table,
    TableHeader,
    TableCell,
    TableRow,
    TableFooter,
    Button,
    Pagination,
  } from '@windmill/react-ui'

class Transactions extends React.Component {
    constructor(props) {
        super(props)
        this.state = {
            transactions: "",
        }
        const [data, setData] = useState([])
    }

    componentDidMount() {
        fetch('http://localhost:2121/transactions')
        .then(results => {
        return results.json()
        }).then(jsonResponse => {
            if (jsonResponse.transactions) {
                return jsonResponse.transactions.map(transaction => {
                    return {
                        id: transaction.id,
                        description: transaction.description,
                        category: transaction.category,
                        payee: transaction.payee,
                        block_height: transaction.block_height,
                        txid: transaction.txid,
                        balance_change: transaction.balance_change,
                        account: transaction.account,
                        address: transaction.address,
                        fee: transaction.fee,
                        size: transaction.size,
                        date: transaction.createdAt
                    }
                })
            }
            //this.setState({transactions: this.state.transactions})
        }).then(this.setState({transactions: transactions}))
    }

    render() {
        return (
            <TableContainer>
        <Table>
          <TableHeader>
            <tr>
              <TableCell>Date</TableCell>
              <TableCell>Payee</TableCell>
              <TableCell>Description</TableCell>
              <TableCell>Category</TableCell>
              <TableCell>Amount</TableCell>
              <TableCell>Balance</TableCell>
              <TableCell>Actions</TableCell>
            </tr>
          </TableHeader>
          <TableBody>
            {this.state.transactions.map((transaction, i) => (
              <TableRow key={i}>
                <TableCell>
                  <span className="text-sm">{new Date(transaction.date).toLocaleDateString()}</span>
                </TableCell>
                <TableCell>
                    <p className="font-semibold">{transaction.payee}</p>
                </TableCell>
                <TableCell>
                    <p className="text-xs text-gray-600 dark:text-gray-400">{transaction.description}</p>
                </TableCell>
                <TableCell>
                  <span className="text-sm"> {transaction.category}</span>
                </TableCell>
                <TableCell>
                  <span className="text-sm">$ {transaction.balance_change}</span>
                </TableCell>
                <TableCell>
                  <span className="text-sm">$ {transaction.fee}</span>
                </TableCell>
                <TableCell>
                  <div className="flex items-center space-x-4">
                    <Button layout="link" size="icon" aria-label="Edit">
                      <EditIcon className="w-5 h-5" aria-hidden="true" />
                    </Button>
                    <Button layout="link" size="icon" aria-label="Delete">
                      <MenuIcon className="w-5 h-5" aria-hidden="true" />
                    </Button>
                  </div>
                </TableCell>
              </TableRow>
            ))}
          </TableBody>
        </Table>
        <TableFooter>
          {/*<Pagination
            totalResults={totalResults}
            resultsPerPage={resultsPerPage}
            label="Table navigation"
            onChange={onPageChange}
          />*/}
        </TableFooter>
      </TableContainer>
        )
    }
}

export default Transactions
