import React from 'react'
import { EditIcon, DropdownIcon, TrashIcon } from '../icons'
import Server from '../utils/server'

import {
    TableCell,
    TableRow,
    Button,
    Input,
  } from '@windmill/react-ui'

export class Account extends React.Component {
  constructor(props) {
    super(props)
    this.state = {
      editView: false,
      account: this.props.account,
      savedAccount: this.props.savedAccount
    }
    this.onEditButtonClicked = this.onEditButtonClicked.bind(this)
    this.handleTextChange = this.handleTextChange.bind(this)
    this.onSubmitButtonClicked = this.onSubmitButtonClicked.bind(this)

    this.updateName = this.updateName.bind(this);
    this.updateNotes = this.updateNotes.bind(this);
    this.updateType = this.updateType.bind(this);
  }

  hasAllRequiredFields() {
    const account = this.state.account;
    if (!account.name || !account.accounttype) {
      return false;
    }
    return true;
  }

  hasChanges() {
    const account = this.state.account;
    const savedAccount = this.state.savedAccount;
    if (!savedAccount) {
      return false;
    }

    if (account.name === savedAccount.name &&
        account.notes === savedAccount.notes &&
        account.accounttype === savedAccount.accounttype) {
      return false;
    }

    return true;
  }

  onEditButtonClicked(){
    console.log(this.state.account.name)
    this.setState({
      editView: !this.state.editView

    })
  }

  handleTextChange(event) {
    this.setState({
      name: event.target.value,
      //notes: event.target.getAttribute("notes").value,
      //accountType: event.getAttribute("accountType").target.value
    })
  }

  updateName(event) {
    const account = JSON.parse(JSON.stringify(this.state.account))
    account.name = event.target.value
    this.setState({account: account})
  }

  updateNotes(event) {
    const account = JSON.parse(JSON.stringify(this.state.account))
    account.notes = event.target.value
    this.setState({account: account})
  }

  updateType(event) {
    const account = JSON.parse(JSON.stringify(this.state.account))
    account.accounttype = event.target.value
    this.setState({account: account})
  }

  onSubmitButtonClicked() {
    Server.editAccount(JSON.stringify(this.state.account)).then(account => {
      this.setState({
        account: account,
        //savedAccount: JSON.parse(JSON.stringify(account))
      })
    })
  }

  getAccountType(typeId) {
    let accountType = typeId
    switch (accountType) {
      case 1:
        return 'Hot Wallet';
      case 2: 
        return 'Vault';
      case 3:
        return 'Expense';
      case 4:
        return 'Income';
      default:
        return '';
    }
  }

  render() {
      const editClicked = this.state.editView
      if (editClicked) {
        return (
          <TableRow key={this.props.account.id}>
                <TableCell>
                  <Input 
                    id="name"
                    className="text-sm"
                    value={this.state.account.name}
                    onChange={this.updateName}
                  />
                </TableCell>
                <TableCell>
                  <Input 
                    id="notes"
                    className="text-sm"
                    value={this.state.account.notes}
                    onChange={this.updateNotes}
                  />
                </TableCell>
                <TableCell>
                  <Input 
                    id="accountType"
                    className="text-sm"
                    value={this.state.account.accounttype}
                    onChange={this.updateType}
                    //placeholder={this.getAccountType(this.props.account.accounttype)}
                  />
                </TableCell>
                <TableCell>
                  <span className="text-sm">{this.props.account.balance} sats</span>
                </TableCell>
                <TableCell>
                  <div className="flex items-center space-x-4">
                    <Button aria-label="Delete" block size="small" onClick={this.onSubmitButtonClicked}>
                      Save
                    </Button>
                    <Button layout="outline" aria-label="Cancel" onClick={this.onEditButtonClicked} block size="small">
                      Cancel
                    </Button>
                  </div>
                </TableCell>
              </TableRow>
        )
      }

        return (
            <TableRow key={this.props.account.id}>
                <TableCell>
                    <a href="/app/Wallet"><p className="text-sm">{this.props.account.name}</p></a>
                </TableCell>
                <TableCell>
                  <span className="text-sm">{this.props.account.notes}</span>
                </TableCell>
                <TableCell>
                  <span className="text-sm">{this.getAccountType(this.props.account.accounttype)}</span>
                </TableCell>
                <TableCell>
                  <span className="text-sm">{this.props.account.balance} sats</span>
                </TableCell>
                <TableCell>
                  <div className="flex items-center space-x-4">
                    <Button layout="link" size="icon" aria-label="Delete">
                      <DropdownIcon className="w-5 h-5" aria-hidden="true" />
                    </Button>
                    <Button layout="link" size="icon" aria-label="Edit" onClick={this.onEditButtonClicked}>
                      <EditIcon className="w-5 h-5" aria-hidden="true" />
                    </Button>
                    <Button layout="link" size="icon" aria-label="Delete">
                      <TrashIcon className="w-5 h-5" aria-hidden="true" />
                    </Button>
                  </div>
                </TableCell>
                {/* To Add In Dropdown

                <TableCell>
                  <span className="text-sm">{this.props.account.xpub}</span>
                </TableCell>

                <TableCell>
                  <span className="text-sm">{new Date(this.props.account.birthday).toLocaleDateString()}</span>
                </TableCell>

              */}
              </TableRow>

        )
    }
}

// export default Account
