import React from 'react'
import { EditIcon, DropdownIcon, TrashIcon } from '../icons'
import Server from '../utils/server'

import {
    TableCell,
    TableRow,
    Button,
    Input,
  } from '@windmill/react-ui'

export class Account extends React.Component {
  // TODO: Use setters (check non-empty string) and event handlers to update state & push changes to database
  constructor(props) {
    super(props)
    this.state = {
      editView: false,
      account: null
    }
    this.onEditButtonClicked = this.onEditButtonClicked.bind(this)
    this.handleTextChange = this.handleTextChange.bind(this)
    this.onSubmitButtonClicked = this.onSubmitButtonClicked.bind(this)

    this.updateName = this.updateName.bind(this);
    this.updateNotes = this.updateNotes.bind(this);
    this.updatePayee = this.updatePayee.bind(this);
  }

  componentDidMount() {
    this.setState({
      account: this.props.account,
      savedAccount: JSON.parse(JSON.stringify(this.props.account))
    })
  }

  onEditButtonClicked(){
    this.setState({
      editView: !this.state.editView
    })
  }

  handleTextChange(event) {
    this.setState({
      name: event.target.value,
      //notes: event.target.getAttribute("notes").value,
      //accountType: event.getAttribute("accountType").target.value
    })
  }

  updateName() {

  }

  updateNotes() {

  }

  updatePayee() {

  }

  onSubmitButtonClicked() {
    Server.editAccount(this.state)
  }

  getAccountType(typeId) {
    let accountType = typeId
    switch (accountType) {
      case 1:
        return 'Hot Wallet';
      case 2: 
        return 'Vault';
      case 3:
        return 'Expense';
      case 4:
        return 'Income';
      default:
        return '';
    }
  }

  render() {
      const editClicked = this.state.editView
      if (editClicked) {
        return (
          <TableRow key={this.props.account.id}>
                <TableCell>
                  <Input 
                    id="name"
                    className="text-sm"
                    value={this.state.name}
                    onChange={this.handleTextChange}
                  />
                </TableCell>
                <TableCell>
                  <Input 
                    id="notes"
                    className="text-sm"
                    value={this.state.notes}
                    onChange={this.handleTextChange}
                  />
                </TableCell>
                <TableCell>
                  <Input 
                    id="accountType"
                    className="text-sm"
                    value={this.state.payee}
                    onChange={this.handleTextChange}
                    placeholder={this.getAccountType(this.props.account.accounttype)}
                  />
                </TableCell>
                <TableCell>
                  <span className="text-sm">{this.props.account.balance} sats</span>
                </TableCell>
                <TableCell>
                  <div className="flex items-center space-x-4">
                    <Button aria-label="Delete" block size="small" onClick={this.onSubmitButtonClicked}>
                      Save
                    </Button>
                    <Button layout="outline" aria-label="Cancel" onClick={this.onEditButtonClicked} block size="small">
                      Cancel
                    </Button>
                  </div>
                </TableCell>
              </TableRow>
        )
      }

        return (
            <TableRow key={this.props.account.id}>
                <TableCell>
                    <a href="/app/Wallet"><p className="text-sm">{this.props.account.name}</p></a>
                </TableCell>
                <TableCell>
                  <span className="text-sm">{this.props.account.notes}</span>
                </TableCell>
                <TableCell>
                  <span className="text-sm">{this.getAccountType(this.props.account.accounttype)}</span>
                </TableCell>
                <TableCell>
                  <span className="text-sm">{this.props.account.balance} sats</span>
                </TableCell>
                <TableCell>
                  <div className="flex items-center space-x-4">
                    <Button layout="link" size="icon" aria-label="Delete">
                      <DropdownIcon className="w-5 h-5" aria-hidden="true" />
                    </Button>
                    <Button layout="link" size="icon" aria-label="Edit" onClick={this.onEditButtonClicked}>
                      <EditIcon className="w-5 h-5" aria-hidden="true" />
                    </Button>
                    <Button layout="link" size="icon" aria-label="Delete">
                      <TrashIcon className="w-5 h-5" aria-hidden="true" />
                    </Button>
                  </div>
                </TableCell>
                {/* To Add In Dropdown

                <TableCell>
                  <span className="text-sm">{this.props.account.xpub}</span>
                </TableCell>

                <TableCell>
                  <span className="text-sm">{new Date(this.props.account.birthday).toLocaleDateString()}</span>
                </TableCell>

              */}
              </TableRow>

        )
    }
}

// export default Account
